using FluentAssertions;
using Microsoft.VisualStudio.TestPlatform.TestHost;

namespace TestProject1
{
	public class CalculatePinTests
	{
		private readonly string[] _instructions1 = { "LRULL", "RRDDDUD", "ULRDL", "UUUD" };
		private readonly string[] _instructions2 = { "", "",                   "", "",      ""
			};

		[Theory]
		[InlineData(true)]
		[InlineData(false)]
		public void CalculateCodeMustReturnValidValue(bool startFromLastPosition)
		{
			var result = CalculatePin.CalculateCode(_instructions1, startFromLastPosition);
			if (startFromLastPosition)
				result.Should().Be("1985");
			else
				result.Should().Be("1945");
		}

		[Theory]
		[InlineData(true)]
		[InlineData(false)]
		public void CalculateCodeMustThrowWhenInstructionIsEmptyOrNull(bool startFromLastPosition)
		{


			Assert.Throws<ArgumentException>(() => CalculatePin.CalculateCode(new string[] { }, startFromLastPosition));
			Assert.Throws<ArgumentException>(() => CalculatePin.CalculateCode(null, startFromLastPosition));


		}

		[Theory]
		[InlineData(true)]
		[InlineData(false)]
		public void CalculateCodeMustReturnValidForLargeValue(bool startFromLastPosition)
		{
			var result = CalculatePin.CalculateCode(_instructions2, startFromLastPosition);
			if (startFromLastPosition)
				result.Should().Be("69642");
			else
				result.Should().Be("69642");
		}



	}
}